import React, { useState, useEffect } from 'react';
import { Package, Store, BarChart3 } from 'lucide-react';
import { useApp } from '../../context/AppContext';
import VendorOrders from './VendorOrders';
import VendorRestaurants from './VendorRestaurants';
import VendorInventory from './VendorInventory';

export default function VendorDashboard() {
  const { apiCall, setLoading, showNotification } = useApp();
  const [activeTab, setActiveTab] = useState('orders');
  const [orders, setOrders] = useState([]);
  const [restaurants, setRestaurants] = useState([]);
  const [inventory, setInventory] = useState([]);

  useEffect(() => {
    loadVendorData();
  }, []);

  const loadVendorData = async () => {
    setLoading(true);
    try {
      const [ordersData, restaurantsData, inventoryData] = await Promise.all([
        apiCall('/orders/vendor/orders'),
        apiCall('/restaurants/vendor/my-restaurants'),
        apiCall('/inventory/vendor')
      ]);

      setOrders(ordersData.orders || []);
      setRestaurants(restaurantsData.restaurants || []);
      setInventory(inventoryData.inventory || []);
    } catch (error) {
      showNotification('Failed to load vendor data', 'error');
    } finally {
      setLoading(false);
    }
  };

  const updateOrderStatus = async (orderId, status) => {
    setLoading(true);
    try {
      await apiCall(`/orders/${orderId}/status`, {
        method: 'PATCH',
        body: JSON.stringify({ order_status: status })
      });
      showNotification('Order status updated successfully', 'success');
      await loadVendorData();
    } catch (error) {
      showNotification('Failed to update order status', 'error');
    } finally {
      setLoading(false);
    }
  };

  const restockItem = async (itemId, quantity, reason) => {
    setLoading(true);
    try {
      await apiCall('/inventory/restock', {
        method: 'POST',
        body: JSON.stringify({ item_id: itemId, quantity, reason: reason || 'Manual restock' })
      });
      showNotification('Item restocked successfully', 'success');
      await loadVendorData();
    } catch (error) {
      showNotification('Failed to restock item', 'error');
    } finally {
      setLoading(false);
    }
  };

  const tabs = [
    { id: 'orders', label: 'Orders', icon: Package, count: orders.length },
    { id: 'restaurants', label: 'My Store', icon: Store, count: restaurants.length },
    { id: 'inventory', label: 'Inventory', icon: BarChart3, count: inventory.length }
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* Dashboard Header */}
      <div className="mb-8">
        <h1 className="text-4xl font-bold mb-2">Vendor Dashboard</h1>
        <p className="text-gray-600">Manage your store, orders, and inventory</p>
      </div>

      {/* Tabs */}
      <div className="bg-white rounded-xl shadow-sm mb-6 overflow-hidden">
        <div className="flex border-b">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex-1 px-6 py-4 font-semibold transition-all flex items-center justify-center gap-3 ${
                  activeTab === tab.id
                    ? 'text-red-600 border-b-2 border-red-600 bg-red-50'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                <Icon size={20} />
                <span>{tab.label}</span>
                {tab.count > 0 && (
                  <span className={`px-2 py-1 rounded-full text-xs font-bold ${
                    activeTab === tab.id ? 'bg-red-600 text-white' : 'bg-gray-200 text-gray-700'
                  }`}>
                    {tab.count}
                  </span>
                )}
              </button>
            );
          })}
        </div>
      </div>

      {/* Tab Content */}
      <div className="bg-white rounded-xl shadow-sm p-6">
        {activeTab === 'orders' && (
          <VendorOrders orders={orders} updateOrderStatus={updateOrderStatus} />
        )}
        {activeTab === 'restaurants' && (
          <VendorRestaurants restaurants={restaurants} />
        )}
        {activeTab === 'inventory' && (
          <VendorInventory inventory={inventory} restockItem={restockItem} />
        )}
      </div>
    </div>
  );
}
